$(function(){$("#myform").validate({rules:{user:{required:!0,isUser:!0},pwd:{required:!0,isPwd:!0},checkPwd:{equalTo:"#pwd"}},messages:{user:{required:"请输入账号"},pwd:{required:"请输入密码"},checkPwd:{equalTo:"两次密码不一致"}}}),jQuery.validator.addMethod("isUser",function(e,r){return this.optional(r)||/^[A-z]\w{5,11}$/.test(e)},"6到12位首字符必须是字母，后面(字母、数字、下划线)"),jQuery.validator.addMethod("isPwd",function(e,r){return this.optional(r)||/^[A-z]\w{5,11}$/.test(e)},"输入6-12正确密码")});